JavaScript<pre><code>// docs/javascripts/keyboard.js
document.addEventListener('DOMContentLoaded', function() {
    // 键盘导航
    setupKeyboardNav();

    // 搜索快捷键
    setupSearchShortcut();
});

// 键盘导航
function setupKeyboardNav() {
    document.addEventListener('keydown', function(e) {
        // ALT + 方向键导航
        if (e.altKey) {
            switch(e.key) {
                case 'ArrowLeft':  // 上一页
                    navigatePage('prev');
                    break;
                case 'ArrowRight':  // 下一页
                    navigatePage('next');
                    break;
                case 'ArrowUp':    // 回到顶部
                    window.scrollTo({top: 0, behavior: 'smooth'});
                    break;
                case 'ArrowDown':  // 到达底部
                    window.scrollTo({
                        top: document.documentElement.scrollHeight,
                        behavior: 'smooth'
                    });
                    break;
            }
        }
    });
}

// 搜索快捷键
function setupSearchShortcut() {
    document.addEventListener('keydown', function(e) {
        // 按下 '/' 键触发搜索
        if (e.key === '/' &amp;&amp; !e.ctrlKey &amp;&amp; !e.altKey &amp;&amp; !e.metaKey) {
            e.preventDefault();
            const searchInput = document.querySelector('.md-search__input');
            if (searchInput) {
                searchInput.focus();
            }
        }
    });
}
